function get_msje(){if(document.msje_calc.age.value.length==0){alert('Please enter your age.');return false}if(document.msje_calc.lbs.value.length==0&&document.msje_calc.kg.value.length==0){alert('Please enter your weight.');return false}if(document.msje_calc.inch.value.length==0&&document.msje_calc.cm.value.length==0){alert('Please enter your height.');return false}if(document.msje_calc.inch.value.length!==0&&document.msje_calc.cm.value.length!==0&&document.msje_calc.lbs.value.length!==0&&document.msje_calc.kg.value.length!==0){alert('Please enter either metric or imperial values only.');return false}if(document.msje_calc.lbs.value.length!==0&&document.msje_calc.inch.value.length!==0){var weight=(0.453592*(document.msje_calc.lbs.value));var height=(2.54*(document.msje_calc.inch.value))}else if(document.msje_calc.kg.value.length!==0&&document.msje_calc.cm.value.length!==0){var weight=document.msje_calc.kg.value;var height=document.msje_calc.cm.value}if(document.msje_calc.gender[0].checked){document.msje_calc.result_bmr.value=((10*(weight))+(6.25*(height))-(5*(document.msje_calc.age.value))+5)}if(document.msje_calc.gender[1].checked){document.msje_calc.result_bmr.value=((10*(weight))+(6.25*(height))-(5*(document.msje_calc.age.value))-161)}if(document.msje_calc.multiplier[0].checked){document.msje_calc.result_msje.value=(1.2*(document.msje_calc.result_bmr.value))}if(document.msje_calc.multiplier[1].checked){document.msje_calc.result_msje.value=(1.375*(document.msje_calc.result_bmr.value))}if(document.msje_calc.multiplier[2].checked){document.msje_calc.result_msje.value=(1.55*(document.msje_calc.result_bmr.value))}if(document.msje_calc.multiplier[3].checked){document.msje_calc.result_msje.value=(1.725*(document.msje_calc.result_bmr.value))}if(document.msje_calc.multiplier[4].checked){document.msje_calc.result_msje.value=(1.9*(document.msje_calc.result_bmr.value))}document.msje_calc.result_bmr.value=Math.round(document.msje_calc.result_bmr.value);document.msje_calc.result_msje.value=Math.round(document.msje_calc.result_msje.value);document.msje_calc.result_lose.value=Math.round((document.msje_calc.result_msje.value)-500);document.msje_calc.result_gain.value=Math.round(1*(document.msje_calc.result_msje.value)+(500))}