function get_mac() {
    if (document.mac_calc.cal.value.length == 0) {
        alert("Please enter your desired daily caloric consumption.");
        return false
    }
    if (document.mac_calc.cal.value <= 1199) {
        alert("You should be consuming no fewer than 1200 calories a day.");
        return false
    }
    if (document.mac_calc.wdays.value.length == 0) {
        alert("Please enter how many days in a week you workout.");
        return false
    }
    if (document.mac_calc.wdays.value > 7) {
        alert("There are only 7 days in a week.");
        return false
    }
    if (document.mac_calc.cal.value.length !== 0 && document.mac_calc.wdays.value.length !== 0) {
        var e = document.mac_calc.wdays.value;
        var t = 7 - document.mac_calc.wdays.value;
        var n = .2 * document.mac_calc.cal.value;
        var r = document.mac_calc.wdays.value * 1 * (document.mac_calc.cal.value * 1 + n * 1) + t * 1 * (document.mac_calc.cal.value * 1 - n * 1)
    }

    if (r > 7 * document.mac_calc.cal.value) {
        var i = r - 7 * document.mac_calc.cal.value;
        var s = i / e;
        if (document.mac_calc.cal.value - n >= 1200) {
            document.mac_calc.result_wcal.value = Math.round(1 * document.mac_calc.cal.value + 1 * n - s);
            document.mac_calc.result_rcal.value = Math.round(document.mac_calc.cal.value - n)
        } else if (document.mac_calc.cal.value - n < 1200) {
            document.mac_calc.result_wcal.value = document.mac_calc.cal.value;
            document.mac_calc.result_rcal.value = document.mac_calc.cal.value
        }
    } else if (r <= 7 * document.mac_calc.cal.value) {
        var o = 7 * document.mac_calc.cal.value - r;
        var u = o / t;
        if (document.mac_calc.cal.value - n >= 1200) {
            document.mac_calc.result_wcal.value = Math.round(document.mac_calc.cal.value * 1 + n);
            document.mac_calc.result_rcal.value = Math.round(document.mac_calc.cal.value * 1 - 1 * n + 1 * u)
        } else if (document.mac_calc.cal.value - n < 1200) {
            document.mac_calc.result_wcal.value = document.mac_calc.cal.value;
            document.mac_calc.result_rcal.value = document.mac_calc.cal.value
        }
    }
    document.mac_calc.wc_cal.value = Math.round(.35 * document.mac_calc.result_wcal.value);
    document.mac_calc.wp_cal.value = Math.round(.25 * document.mac_calc.result_wcal.value);
    document.mac_calc.wf_cal.value = Math.round(.4 * document.mac_calc.result_wcal.value);
    document.mac_calc.rc_cal.value = Math.round(.15 * document.mac_calc.result_rcal.value);
    document.mac_calc.rp_cal.value = Math.round(.25 * document.mac_calc.result_rcal.value);
    document.mac_calc.rf_cal.value = Math.round(.6 * document.mac_calc.result_rcal.value);
    document.mac_calc.wc_g.value = Math.round(document.mac_calc.wc_cal.value / 4);
    document.mac_calc.wp_g.value = Math.round(document.mac_calc.wp_cal.value / 4);
    document.mac_calc.wf_g.value = Math.round(document.mac_calc.wf_cal.value / 9);
    document.mac_calc.rc_g.value = Math.round(document.mac_calc.rc_cal.value / 4);
    document.mac_calc.rp_g.value = Math.round(document.mac_calc.rp_cal.value / 4);
    document.mac_calc.rf_g.value = Math.round(document.mac_calc.rf_cal.value / 9)
}